        private void dmtext_KeyPress_1(object sender, KeyPressEventArgs e)
        {
            if(e.KeyChar == 46 && dmtext.Text.IndexOf('.') != -1)
            {
                e.Handled = true;
                return;
            }
            if ( !char.IsDigit(e.KeyChar) && e.KeyChar != 8 && e.KeyChar != 46)
            {
                e.Handled = true;
            }
        }

